import React, { useContext } from "react";
import styled from "styled-components";
import { BusContext } from "../context/BusContext";

const Card = styled.div`
  background: #fff;
  border-radius: 1rem;
  box-shadow: 0 1px 9px rgba(102,126,234,0.08);
  margin-bottom: 2rem;
  padding: 1.5rem;
`;

function LiveMap() {
  const { buses } = useContext(BusContext);
  return (
    <div>
      <h2 style={{color:'#667eea'}}>Live Bus Location (Demo)</h2>
      <Card>
        {buses.map(bus => (
          <div key={bus.id} style={{marginBottom:18}}>
            <b>{bus.id}</b>: <span style={{color:'#333'}}>{bus.lat.toFixed(5)},{bus.lng.toFixed(5)}</span>
          </div>
        ))}
      </Card>
      <p>Integrate <b>react-leaflet</b> for full map view in production.</p>
    </div>
  );
}
export default LiveMap;
